# Load Wave[wave,Fs] = wavread("gas_station.wav");#DC offsetsof = wave - mean(wave);#Choose Frame at 50Window = 400;Shift = 160;ShiftIndex = 1:Shift:length(wave);Frame = zeros(1+ceil(max(0,((length(wave)-Window)/Shift))),Window);ind = 1;for i = ShiftIndex  if(i + Window > length(wave))    temp = i + Window;    temp2 = temp - length(sof)-1;    tempF = sof(i:length(sof));    tempP = zeros(temp2,1);    FrameTemp = [tempF;tempP];    for j = 1:1:Window      Frame(ind,j) = FrameTemp(j);    endfor    break;      endif    for j = 1:1:Window    Frame(ind,j) = sof(i+j-1);  endfor  ind = ind+1;  endfor#Find Frame Energy at Frame 50logE = max(-50,log(sum(Frame(50,:).^2)));#Plot Spectrum before pre emphasissofF50 = Frame(50,:);speF50 = sofF50 - (0.97*reshape(sof(160*49:160*49+400-1),1,400));%subplot(2,1,1);%plot(sofF50);%subplot(2,1,2);%plot(speF50);#Plot Spectrum%frequency = 0:Fs/400:Fs;%subplot(2,1,1);%plot(frequency(1:length(sofF50)),abs(fft(sofF50)));%xlabel('frequency (Hz)');%subplot(2,1,2);%plot(frequency(1:length(speF50)),abs(fft(speF50)));%xlabel('frequency (Hz)');# Plot magnitude Spectrum at frame 50 + Paddinghspe = speF50 .* hamming(length(speF50))';hspe = [hspe,zeros(1,512-length(hspe))];dft = fft(hspe,512);dft = abs(dft);#plot magnitude spectrum at frame 50%subplot(1,1,1)%frequency = 0:Fs/512:Fs;%plot(frequency(1:length(dft)),dft);%xlabel('frequency (Hz)');# Choose 257 pointCdft = dft(1:length(dft)/2+1);#Mel Scaleload("mel_filters.mat");%subplot(2,1,1)%plot((0:256)'/256*8000, mel_filters)%title('Mel filter banks')%xlabel('frequency (Hz)')%ylabel('weight')%subplot(2,1,2)%plot(frequency(1:length(Cdft)),Cdft);MelFHy = Cdft * mel_filters;#cal MFSCMFSC = max(-50,log(MelFHy));#cal MFCCC = 1:1:23;MFCC = zeros(23,1);for i = C  jcos = cos((i-1)*pi/23*((1:1:23)-0.5));  MFCC(i) = MFSC * (jcos');endforload("gas_station_mfcc.mat")